local Players,LP,RS,UI=game:GetService"Players",game:GetService"Players".LocalPlayer,game:GetService"RunService",game:GetService"UserInputService"local G=Instance.new("ScreenGui",game.CoreGui)G.Name="TimeBombGUI"local F=Instance.new("Frame",G)F.Size=UDim2.new(0,250,0,120)F.Position=UDim2.new(0.5,-125,0.1,0)F.BackgroundColor3=Color3.fromRGB(40,40,40)F.BorderSizePixel=0 local B=Instance.new("TextButton",F)B.Size=UDim2.new(0.8,0,0.5,0)B.Position=UDim2.new(0.1,0,0.1,0)B.Text="USE TIMEBOMB"B.BackgroundColor3=Color3.fromRGB(200,50,50)B.TextColor3=Color3.new(1,1,1)B.Font=Enum.Font.SourceSansBold local S=Instance.new("TextLabel",F)S.Size=UDim2.new(0.8,0,0.3,0)S.Position=UDim2.new(0.1,0,0.6,0)S.Text="Status: Ready"S.TextColor3=Color3.new(1,1,1)S.BackgroundTransparency=1 local R,N=false,false local function NC(C)if C then for _,P in pairs(C:GetDescendants())do if P:IsA("BasePart")then P.CanCollide=false end end end end local function SP()local T={}for _,O in pairs(workspace:GetDescendants())do if O:IsA("SpawnLocation")then table.insert(T,O)elseif O:IsA("Model")and(O.Name:lower():find"spawn"or O.Name:lower():find"start")then for _,P in pairs(O:GetDescendants())do if P:IsA("BasePart")then table.insert(T,P)break end end elseif O:IsA("BasePart")and(O.Name:lower():find"spawn"or O.Name:lower():find"start")then table.insert(T,O)end end return T end local function FindTimeBomb()local B=LP:FindFirstChild"Backpack"if B then for _,T in pairs(B:GetChildren())do if T:IsA"Tool"and T.Name:lower()=="timebomb"then return T end end end return nil end local function GP(O)if O:IsA("BasePart")then return O.Position elseif O:IsA("Model")then local P=O.PrimaryPart if P then return P.Position else for _,Pt in pairs(O:GetDescendants())do if Pt:IsA("BasePart")then return Pt.Position end end end elseif O:IsA("SpawnLocation")then return O.Position end return Vector3.new(0,0,0)end local function UT(T,C)if T and T:IsA"Tool"then local CT=C:FindFirstChildOfClass"Tool"if CT then CT.Parent=LP.Backpack end T.Parent=C wait(0.3)if T:FindFirstChild"Handle"then for i=1,3 do T:Activate()wait(0.1)end wait(0.5)end T.Parent=LP.Backpack return true end return false end local function ST()if R then return end R=true S.Text="Status: Starting..."local C=LP.Character if not C or not C:FindFirstChild"HumanoidRootPart"then S.Text="Status: Character not found"R=false return end N=true NC(C)S.Text="Status: Finding TimeBomb..."wait(1)local TB=FindTimeBomb()if not TB then S.Text="Status: TimeBomb not found!"R=false N=false return end S.Text="Status: Using: "..TB.Name wait(1)local SPs=SP()if#SPs==0 then S.Text="Status: No spawn points found"R=false N=false return end S.Text="Status: Found "..#SPs.." points"wait(1)local HRP=C.HumanoidRootPart for i,Sp in ipairs(SPs)do if not R then break end S.Text="Status: Point "..i.."/"..#SPs local Pos=GP(Sp)HRP.CFrame=CFrame.new(Pos+Vector3.new(0,5,0))wait(0.5)S.Text="Status: Placing TimeBomb"local Suc=UT(TB,C)if Suc then S.Text="Status: Bomb placed at point "..i else S.Text="Status: Failed to place bomb"end wait(1)end S.Text="Status: Completed!"R=false N=false end B.MouseButton1Click:Connect(function()if not R then ST()else R=false S.Text="Status: Stopped"end end)RS.Stepped:Connect(function()if N and LP.Character then NC(LP.Character)end end)UI.InputBegan:Connect(function(I)if I.KeyCode==Enum.KeyCode.F and UI:IsKeyDown(Enum.KeyCode.LeftControl)then if not R then ST()else R=false S.Text="Status: Stopped by hotkey"end end end)print("TimeBomb Placer Script loaded! Will use TimeBomb on all spawn points.")
