local Players,LP,RS,UI=game:GetService"Players",game:GetService"Players".LocalPlayer,game:GetService"RunService",game:GetService"UserInputService"local G=Instance.new("ScreenGui",game.CoreGui)G.Name="TeleportBombGUI"local F=Instance.new("Frame",G)F.Size=UDim2.new(0,200,0,100)F.Position=UDim2.new(0.5,-100,0.1,0)F.BackgroundColor3=Color3.fromRGB(40,40,40)F.BorderSizePixel=0 local B=Instance.new("TextButton",F)B.Size=UDim2.new(0.8,0,0.6,0)B.Position=UDim2.new(0.1,0,0.2,0)B.Text="START TELEPORT BOMB"B.BackgroundColor3=Color3.fromRGB(200,50,50)B.TextColor3=Color3.new(1,1,1)B.Font=Enum.Font.SourceSansBold local S=Instance.new("TextLabel",F)S.Size=UDim2.new(0.8,0,0.2,0)S.Position=UDim2.new(0.1,0,0.8,0)S.Text="Status: Ready"S.TextColor3=Color3.new(1,1,1)S.BackgroundTransparency=1 local R,N=false,false local function NC(C)if C then for _,P in pairs(C:GetDescendants())do if P:IsA("BasePart")then P.CanCollide=false end end end end local function SP()local T={}for _,L in pairs({workspace,workspace:FindFirstChild"Map",workspace:FindFirstChild"Doomspire"})do if L then for _,O in pairs(L:GetDescendants())do if O:IsA("SpawnLocation")or O.Name:lower():find"spawn"or O.Name:lower():find"team"or(O:IsA("Part")and O.BrickColor==BrickColor.new"Bright blue")then table.insert(T,O)end end end end return T end local function BT()local B=LP:FindFirstChild"Backpack"if B then for _,T in pairs(B:GetChildren())do if T:IsA"Tool"and(T.Name:lower():find"bomb"or T.Name:lower():find"explosive"or T.Name=="Bomb")then return T end end end return nil end local function TPB()if R then return end R=true S.Text="Status: Running..."local C=LP.Character if not C or not C:FindFirstChild"HumanoidRootPart"then S.Text="Status: Character not found"R=false return end local H,Hrp=C:FindFirstChild"Humanoid",C.HumanoidRootPart N=true NC(C)local Bomb=BT()if not Bomb then S.Text="Status: No bomb found"R=false N=false return end local SPs=SP()if#SPs==0 then S.Text="Status: No spawn points found"R=false N=false return end S.Text="Status: Found "..#SPs.." spawn points"wait(1)for i,Sp in ipairs(SPs)do if not R then break end S.Text="Status: Teleporting to point "..i.."/"..#SPs Hrp.CFrame=CFrame.new(Sp.Position+Vector3.new(0,3,0))wait(0.3)Bomb.Parent=C wait(0.2)if Bomb:FindFirstChild"Handle"then Bomb:Activate()wait(0.1)Bomb.Parent=LP.Backpack end wait(0.5)end S.Text="Status: Completed!"R=false N=false end B.MouseButton1Click:Connect(function()if not R then TPB()else R=false S.Text="Status: Stopped"end end)RS.Stepped:Connect(function()if N and LP.Character then NC(LP.Character)end end)UI.InputBegan:Connect(function(I)if I.KeyCode==Enum.KeyCode.F and UI:IsKeyDown(Enum.KeyCode.LeftControl)then if not R then TPB()else R=false S.Text="Status: Stopped by hotkey"end end end)print"Doomspire Teleport Bomb Script loaded!"
