local Players,LP,RS,UI=game:GetService"Players",game:GetService"Players".LocalPlayer,game:GetService"RunService",game:GetService"UserInputService"local G=Instance.new("ScreenGui",game.CoreGui)G.Name="TeleportBombGUI"local F=Instance.new("Frame",G)F.Size=UDim2.new(0,250,0,120)F.Position=UDim2.new(0.5,-125,0.1,0)F.BackgroundColor3=Color3.fromRGB(40,40,40)F.BorderSizePixel=0 local B=Instance.new("TextButton",F)B.Size=UDim2.new(0.8,0,0.5,0)B.Position=UDim2.new(0.1,0,0.1,0)B.Text="USE 5TH ITEM"B.BackgroundColor3=Color3.fromRGB(200,100,50)B.TextColor3=Color3.new(1,1,1)B.Font=Enum.Font.SourceSansBold local S=Instance.new("TextLabel",F)S.Size=UDim2.new(0.8,0,0.3,0)S.Position=UDim2.new(0.1,0,0.6,0)S.Text="Status: Ready"S.TextColor3=Color3.new(1,1,1)S.BackgroundTransparency=1 local R,N=false,false local function NC(C)if C then for _,P in pairs(C:GetDescendants())do if P:IsA("BasePart")then P.CanCollide=false end end end end local function SP()local T={}for _,L in pairs({workspace,workspace:FindFirstChild"Map",workspace:FindFirstChild"Doomspire"})do if L then for _,O in pairs(L:GetDescendants())do if O:IsA("SpawnLocation")or O.Name:lower():find"spawn"or O.Name:lower():find"team"or O.Name:lower():find"point"then table.insert(T,O)end end end end return T end local function GT5()local B=LP:FindFirstChild"Backpack"if B then local Tools={}for _,Tool in pairs(B:GetChildren())do if Tool:IsA"Tool"then table.insert(Tools,Tool)end end if#Tools>=5 then return Tools[5]elseif#Tools>0 then return Tools[#Tools]end end return nil end local function IU(T,C)if T and T:IsA"Tool"then T.Parent=C wait(0.2)if T:FindFirstChild"Handle"then T:Activate()local P=Instance.new("Part",workspace)P.Name="Used_"..T.Name P.Size=Vector3.new(3,3,3)P.Position=C.HumanoidRootPart.Position+Vector3.new(0,1.5,0)P.BrickColor=BrickColor.new("Bright orange")P.Anchored=true P.CanCollide=false P.Material=Enum.Material.Neon local BG=Instance.new("BillboardGui",P)BG.Size=UDim2.new(4,0,4,0)BG.Adornee=P local TL=Instance.new("TextLabel",BG)TL.Size=UDim2.new(1,0,1,0)TL.Text=T.Name TL.TextScaled=true TL.BackgroundTransparency=1 TL.TextColor3=Color3.new(1,1,1)wait(0.3)T.Parent=LP.Backpack return P end end return nil end local function TPB()if R then return end R=true S.Text="Status: Running..."local C=LP.Character if not C or not C:FindFirstChild"HumanoidRootPart"then S.Text="Status: Character not found"R=false return end local Hrp=C.HumanoidRootPart N=true NC(C)local FifthTool=GT5()if not FifthTool then S.Text="Status: No 5th tool found"R=false N=false return end local SPs=SP()if#SPs==0 then S.Text="Status: No spawn points found"R=false N=false return end S.Text="Status: Using: "..FifthTool.Name wait(1)for i,Sp in ipairs(SPs)do if not R then break end S.Text="Status: Point "..i.."/"..#SPs Hrp.CFrame=CFrame.new(Sp.Position+Vector3.new(0,3,0))wait(0.4)S.Text="Status: Placing "..FifthTool.Name local Placed=IU(FifthTool,C)if Placed then S.Text="Status: Placed at point "..i wait(0.7)end end S.Text="Status: Completed! Used "..FifthTool.Name.." "..#SPs.." times"R=false N=false end B.MouseButton1Click:Connect(function()if not R then TPB()else R=false S.Text="Status: Stopped"end end)RS.Stepped:Connect(function()if N and LP.Character then NC(LP.Character)end end)UI.InputBegan:Connect(function(I)if I.KeyCode==Enum.KeyCode.F and UI:IsKeyDown(Enum.KeyCode.LeftControl)then if not R then TPB()else R=false S.Text="Status: Stopped by hotkey"end end end)print"Fifth Item User Script loaded! Will use the 5th tool from inventory."
